mapper.insert:
insert into orders (number, title, status, user_id, address_book_id, order_time, take_time, finish_time, amount, remark, receiver_id, express_station_id)
VALUES(#{number},#{title},#{status},#{userId},#{addressBookId},#{orderTime},#{takeTime},#{finishTime},#{amount},#{remark},#{receiverId},#{expressStationId}
)

mapper.select:
select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc
